import React, { useState } from "react";

export default function Counter() {
  const [value, setValue] = useState(0);
  const reset = () => {
    setValue(0);
  };
  return (
    <div>
      <h2>Regular counter</h2>
      <h1>{value}</h1>
      <button className="btn" onClick={setValue(value - 1)}>
        Decrease
      </button>
      <button className="btn" onClick={reset}>
        Reset
      </button>
      <button className="btn" onClick={setValue(value + 1)}>
        Increase
      </button>
    </div>
  );
}
