import React from "react";
import Image from "./Image";

export default function Book() {
  const title = "Love this book"
  return (
    <article className="book">
      <Image />
      <h1>{title}</h1>
      <Author />
    </article>
  );
}

const Author = () => {
  return <h1>John Doe</h1>;
};
